<table align="center"><tr><td style="padding:2px;" ><script language="JavaScript">function get_roundEMI(X) { return Math.round(X*100)/100 }function getEmi_showpay() { if ((document.calc_wigdet.Loan_Amount.value == null || document.calc_wigdet.Loan_Amount.value.length == 0) ||     (document.calc_wigdet.months.value == null || document.calc_wigdet.months.value.length == 0)||     (document.calc_wigdet.rate.value == null || document.calc_wigdet.rate.value.length == 0)) { document.calc_wigdet.pay.value = "Incomplete data";document.calc_wigdet.tot_amount.value = "Incomplete data";document.calc_wigdet.tot_interest.value = "Incomplete data";document.calc_wigdet.yearly_interest.value = "Incomplete data"; } else { var princ = document.calc_wigdet.Loan_Amount.value; var term  = document.calc_wigdet.months.value; var intr   = document.calc_wigdet.rate.value / 1200; var yrs   = document.calc_wigdet.months.value / 12; document.calc_wigdet.pay.value = get_roundEMI(princ * intr / (1 - (Math.pow(1/(1 + intr), term)))); document.calc_wigdet.tot_amount.value = get_roundEMI(document.calc_wigdet.pay.value * term); document.calc_wigdet.tot_interest.value = get_roundEMI(document.calc_wigdet.tot_amount.value - princ); document.calc_wigdet.yearly_interest.value = get_roundEMI(document.calc_wigdet.tot_interest.value / yrs); }} function getintOnly(e){if(e.value.length>0){e.value=e.value.replace(/[^\d]+/g,"")}} </script> <form method="post" name="calc_wigdet" id="calc_wigdet"> <table border="0" align="center" cellpadding="0" cellspacing="4"  style="border:#CCC 1px solid; border-radius:5px;" ><tr>    <td height="25" colspan="2" style="font-size:14px; border-bottom:#999 solid thin;" align="center"><h3  style="font-size:14px;">Calculate  Loans EMI Online</h3></td></tr><tr><td width="55%" align="left" valign="middle"  style="font-size:14px; color:#999;"><strong>Loan Amount</strong></td><td width="45%"><input name='Loan_Amount' type='text'  style="width:124px; float:left; margin-top:2px;" id='Loan_Amount' onkeypress="getintOnly(this);" onkeydown="getintOnly(this);" onkeyup="getintOnly(this);" value='100000' maxlength='9' /></td>    </tr><tr>        <td align="left" valign="middle"    style="font-size:14px !important; font-weight:bold !important; color:#999;"  >Interest Rate (Reducing) </td>        <td  style=" font-size:12px; "><input name='rate' type='text' style="width:50px;" value='13' maxlength='5' /> % Per Annum </td>      </tr>	   <tr>        <td align="left" valign="middle"  style="font-size:14px; color:#999;"   ><strong>Loan Tenure</strong></td><td  style=" font-size:12px; "><input name='months' type='text' style="width:50px;" onkeypress="getintOnly(this);" onkeydown="getintOnly(this);" onkeyup="getintOnly(this);" value='36' maxlength='5'  /> (in Months)</td>      </tr>            <tr><td>&nbsp;</td>        <td  valign="middle"  style="   font-size:12px; font-weight:normal;" ><input name="button" type="button" class="hl-get-quotebtn" onclick='getEmi_showpay()' value="Calculate" />      </td>        </tr>         <tr>        <td colspan="2" align="center"><table border="0" cellpadding="1" cellspacing="2" width="100%">         <tr>        <td width="52%" align="left" valign="middle" bgcolor="#00AFEA"  style="font-size:12px; color:#FFF; padding-left:3px;" >Monthly EMI</td>        <td width="48%" bgcolor="#00AFEA" style="font-size:12px; color: #FFF; padding-left:3px;"><table><tr><td><img src="http://www.deal4loans.com/images/cal-rupee_smbl.png"  border="0" /></td><td><input name="pay" type="text" style="font-weight:bold; width:100px; background:#00AFEA; font-size:12px; color: #FFF; border:none;" value="3369.4" readonly/></td></tr></table>           </td>      </tr>         <tr>        <td align="left" valign="middle" bgcolor="#4572a7"  style="font-size:12px; color: #FFF; padding-left:3px;">Total Amount with Interest</td>        <td bgcolor="#4572a7" style="font-size:12px; color: #FFF; padding-left:3px;"><table><tr><td><img src="http://www.deal4loans.com/i