	if (Form.mobile.value.length < 10 )
	{
			alert("Please Enter 10 Digits"); 
			Form.mobile.focus();
			return false;
	}
	if (Form.mobile.value.charAt(0)!="9")
	{
			alert("The number should start only with 9");
			Form.mobile.focus();
			return false;
	}
}
